package loops;

public class P1to100EvenumbersUsngIF {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=100;i++) {
			if(i%2==0)
			{
				System.out.println(" Even number is "+i);
			} 
			else {
				System.out.println(" odd number is "+i);
			}
		}

	}

}
